import React, { useEffect, useState } from 'react';
import './Homepage.css';
import SearchBar from './searchBar'; 
import EventPreview from './EventPreview';

const Homepage = () => {
    const [events, setEvents] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [hasMore, setHasMore] = useState(false);
    const [lastEventId, setLastEventId] = useState(null);
    
    const fetchEvents = async (initialLoad = true) => {
        try {
            setIsLoading(true);
            setError(null);

            // Build the API URL
            let url = 'http://localhost:4000/events/recommended?limit=6';
            if (!initialLoad && lastEventId) {
                url += `&lastEventId=${lastEventId}`;
            }

            const response = await fetch(url);
            
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            
            const data = await response.json();
            
            if (!data.success) {
                throw new Error(data.message || 'Failed to fetch events');
            }

            // Update events state
            if (initialLoad) {
                setEvents(data.events);
            } else {
                setEvents(prev => [...prev, ...data.events]);
            }

            // Update pagination states
            setLastEventId(data.lastEventId || null);
            setHasMore(data.hasMore || false);
            
        } catch (error) {
            console.error('Fetch error:', error);
            setError(error.message);
        } finally {
            setIsLoading(false);
        }
    };

    useEffect(() => {
        fetchEvents(true);
    }, []);

    const RecommendedHeader = () => (
        <div className="wavy-header-container">
            <svg style={{ position: 'absolute', height: 0 }}>
                <defs>
                    <filter id="dropShadowAbove" x="-20%" y="-20%" width="140%" height="140%">
                        <feGaussianBlur in="SourceAlpha" stdDeviation="3"/>
                        <feOffset dy="-3" result="offsetblur"/>
                        <feFlood floodColor="rgba(0,0,0,0.3)"/>
                        <feComposite in2="offsetblur" operator="in"/>
                        <feMerge>
                            <feMergeNode/>
                            <feMergeNode in="SourceGraphic"/>
                        </feMerge>
                    </filter>
                    <filter id="dropShadowBelow" x="-20%" y="-20%" width="140%" height="140%">
                        <feGaussianBlur in="SourceAlpha" stdDeviation="3"/>
                        <feOffset dy="3" result="offsetblur"/>
                        <feFlood floodColor="rgba(0,0,0,0.3)"/>
                        <feComposite in2="offsetblur" operator="in"/>
                        <feMerge>
                            <feMergeNode/>
                            <feMergeNode in="SourceGraphic"/>
                        </feMerge>
                    </filter>
                </defs>
            </svg>
            
            <svg className="wave-overlap-top" viewBox="0 0 1200 40" preserveAspectRatio="none">
                <path 
                    d="M0,40 C150,0 300,40 450,20 C600,0 750,20 900,30 C1050,40 1200,10 1200,10 L1200,40 L0,40 Z" 
                    fill="#F0C419"
                    filter="url(#dropShadowAbove)"
                />
            </svg>
            
            <div className="wavy-content">
                <h1 className='recommendedText'>Recommended</h1>
            </div>
            
            <svg className="wave-bottom" viewBox="0 0 1200 40" preserveAspectRatio="none">
                <path 
                    d="M0,10 C200,30 300,-5 500,25 C700,5 800,35 1000,15 C1100,5 1150,25 1200,15 L1200,0 L0,0 Z"  
                    fill="#F0C419"
                    filter="url(#dropShadowBelow)"
                />
            </svg>
        </div>
    );

    const handleRetry = () => {
        fetchEvents(true);
    };

    const handleLoadMore = () => {
        fetchEvents(false);
    };

    if (isLoading && events.length === 0) {
        return (
            <div className="homepage">
                <div className="header">
                    <SearchBar />
                    <RecommendedHeader />
                </div>
                <div className="loading">Loading events...</div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="homepage">
                <div className="header">
                    <SearchBar />
                    <RecommendedHeader />
                </div>
                <div className="error">
                    <p>Error: {error}</p>
                    <button onClick={handleRetry} className="retry-button">
                        Try Again
                    </button>
                </div>
            </div>
        );
    }

    return (
        <div className="homepage">
            <div className="header">
                <SearchBar />
                <RecommendedHeader />
            </div>
            <div className="eventsContainer">
                {events.length > 0 ? (
                    <>
                        {events.map(event => (
                            <EventPreview key={event.id} event={event} />
                        ))}
                        {hasMore && (
                            <button 
                                onClick={handleLoadMore} 
                                disabled={isLoading}
                                className="load-more-button"
                            >
                                {isLoading ? 'Loading...' : 'Load More Events'}
                            </button>
                        )}
                    </>
                ) : (
                    <div className="noEvents">
                        <p>No events available</p>
                        <button onClick={handleRetry} className="retry-button">
                            Refresh
                        </button>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Homepage;
