import React, { useState, useEffect } from 'react';
import './Profile.css';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          throw new Error('No token found. Please log in.');
        }

        const response = await fetch('http://localhost:4000/api/user', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });

        console.log('Response:', response); // Log the response object

        if (!response.ok) {
          const errorText = await response.text(); // Log the response body for debugging
          console.error('Error response:', errorText);
          throw new Error('Failed to fetch user data');
        }

        const data = await response.json();
        console.log('User Data:', data);
        setUser(data);
      } catch (err) {
        console.error('Error:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchUserData(); // Call the fetch function
  }, []); // Add empty dependency array to run only once

  if (loading) return <div>Loading...</div>;
  if (error) return <div className="error">Error: {error}</div>;

  return (
    <div className="profileBox">
      <div className="leftProfileBox">
        <h2>Profile Information</h2>
        {user ? (
          <>
            <p className="usernameDisplay">
              <strong>Username:</strong> {user.username}
            </p>
            <p className="emailDisplay">
              <strong>Email:</strong> {user.email}
            </p>
            <p className="passwordDisplay">
              <strong>Password:</strong> ********
            </p>
          </>
        ) : (
          <p>No user data available.</p>
        )}
      </div>
      <div className="rightProfileBox">
        <div className="activeEvents">
          <h2>Active Events</h2>
          <p>No active events at the moment.</p>
        </div>
      </div>
    </div>
  );
};

export default Profile;
