import React from 'react';
import './EventPreview.css';
import { useNavigate } from 'react-router-dom';

const EventPreview = ({ event }) => {
    const navigate = useNavigate();

    const handleClick = () => {
        navigate(`/event/${event.id}`); // Navigate to the event's detail page
    };

    return (
        <div className='previewContainer'>
            <button className='eventAdvertisement' onClick={handleClick}>
                <div className='imageHolder'>
                    {/* Display the event photo */}
                    <img className='eventImage' src={event.photo} alt={event.title} />
                </div>
                <div className='eventInfo'>
                    <div className='topInfo'>
                        {/* Display the event title */}
                        <h1 className='eventTitle'>{event.title}</h1>
                        {/* Display the event date */}
                        <p className='eventDate'><img src="calendar-icon.png" alt="Calendar" />{event.date}</p>
                    </div>
                    <div className='bottomInfo'>
                        {/* Display the event description */}
                        <p className='eventDescription'>{event.description}</p>
                        {/* Display the event location */}
                        <p className='eventLocation'><img src="location-icon.png" alt="Location" />{event.location}</p>
                        <p className='moreInfo'>click for more info...</p>
                    </div>
                </div>
            </button>
        </div>
    );
};

export default EventPreview;
