import React, { useEffect, useState } from 'react';
import './Homepage.css';
import SearchBar from './searchBar'; 
import EventPreview from './EventPreview';

const Homepage = () => {
    const [events, setEvents] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    
    useEffect(() => {
        const fetchEvents = async () => {
            try {
                const response = await fetch('http://localhost:4000/events');
                
                if (!response.ok) {
                    throw new Error('Failed to fetch events');
                }
                
                const data = await response.json();
                setEvents(data);
            } catch (error) {
                setError(error.message);
            } finally {
                setIsLoading(false);
            }
        };

        fetchEvents();
    }, []);

    const RecommendedHeader = () => (
        <div className="wavy-header-container">
          {/* wavyyy */}
          <svg className="wave-overlap-top" viewBox="0 0 1200 40" preserveAspectRatio="none">
            <path 
              d="M0,40 C150,0 300,40 450,20 C600,0 750,20 900,30 C1050,40 1200,10 1200,10 L1200,40 L0,40 Z" 
              fill="#003566"
            />
          </svg>
          
          <div className="wavy-content">
            <h1 className='recommendedText'>Recommended</h1>
          </div>
          
          <svg className="wave-bottom" viewBox="0 0 1200 40" preserveAspectRatio="none">
            <path 
              d="M0,0 C150,30 300,0 450,20 C600,40 750,20 900,10 C1050,0 1200,30 1200,30 L1200,0 L0,0 Z" 
              fill="#003566"
            />
          </svg>
        </div>
      );
    if (isLoading) {
        return (
            <div className="homepage">
                <div className="header">
                    <SearchBar />
                    <RecommendedHeader />
                </div>
                <div className="loading">Loading events...</div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="homepage">
                <div className="header">
                    <SearchBar />
                    <RecommendedHeader />
                </div>
                <div className="error">
                    <p>Error: {error}</p>
                </div>
            </div>
        );
    }

    return (
        <div className="homepage">
            <div className="header">
                <SearchBar />
                <RecommendedHeader />
            </div>
            <div className="eventsContainer">
                {events.length > 0 ? (
                    events.map(event => (
                        <EventPreview key={event.id} event={event} />
                    ))
                ) : (
                    <div className="noEvents">No events available</div>
                )}
            </div>
        </div>
    );
};

export default Homepage;
