import React, { useState } from 'react';
import './SearchBar.css';
import { useNavigate } from 'react-router-dom';

const SearchBar = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [events, setEvents] = useState([]);
  const [fetchError, setFetchError] = useState(false); // New state for fetch errors
  const navigate = useNavigate();

  const handleSearchSubmit = async (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      try {
        setFetchError(false); // Reset error state before fetching
        // Simulate fetching events (replace with actual API call)
        const fetchedEvents = []; // Replace with actual fetch logic
        setEvents(fetchedEvents);

        if (fetchedEvents.length > 0) {
          navigate(`/search?q=${encodeURIComponent(searchQuery)}`);
        }
      } catch (error) {
        setFetchError(true); // Set error state if fetch fails
      }
    }
  };

  return (
    <div className="searchBox">
      <div className="searchContainer">
        <h1>SlugHub</h1>
        <form className="search-form" onSubmit={handleSearchSubmit}>
          <input
            type="text"
            className="search-input"
            placeholder="Search events..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <button type="submit" className="search-button">
            <img className="search-icon" src="/images/search-icon-png-21.png" alt="searchIcon" />
          </button>
        </form>
        {fetchError && <p className="error-message">Failed to fetch events</p>}
        {!fetchError && events.length === 0 && searchQuery.trim() && (
          <p className="no-events-message">No events available</p>
        )}
      </div>
    </div>
  );
};

export default SearchBar;
