import './Loading.css';
import React, { useState, useEffect } from 'react';

const frames = [
  '<span className="ellipse"><img src="/images/slugmation1.png" alt="Loading" className="loading-image" /></span>',
  '<span className="ellipse">.<img src="/images/slugmation2.png" alt="Loading" className="loading-image" /></span>',
  '<span className="ellipse">..<img src="/images/slugmation3.png" alt="Loading" className="loading-image" /></span>',
];

const Loading = () => {
  const [frameIndex, setFrameIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setFrameIndex((prevIndex) => (prevIndex + 1) % frames.length);
    }, 500);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="loading-container">
      <span
        className="loadingAnimation"
        dangerouslySetInnerHTML={{ __html: frames[frameIndex] }}
      />
      <img src="/../public/images/slugmation1.png" alt="Loading" className="loading-image" />
    </div>
  );
};

export default Loading;
