import './CreateEvent.css';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';   


const CreateEvent = () => {
  const navigate = useNavigate();
  const [values, setValues] = useState({
    title: '',
    description: '',
    photo: '',
    date: '',  
    startTime: '',
    endTime: '',
    location: '',
  });

  const handleChange = (event) => {
    setValues({ ...values, [event.target.name]: event.target.value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Submitting values:', values);  // Debugging log

    axios
      .post('http://localhost:4000/create-event', values)
      .then((res) => {
        console.log('Registered successfully');
        // Optionally redirect to login page after successful registration
        navigate('/');
      })
      .catch((err) => {
        console.error('Error:', err);
      });
  };

  return (
    <div className="createPage">
      <div className='createBox'>
        <form className='infoFill' onSubmit={handleSubmit}>
          <div className='leftBox'>
            <input
              className='eventImageUpload'
              type='image'
              placeholder='Upload image'
              name='photo'
              value={values.photo}
              onChange={handleChange}
            />
            <br />
            <input
              className='eventNameUpload'
              type='text'
              placeholder='Event title...'
              name='title'
              value={values.title}
              onChange={handleChange}
            />
            <br />
            <input
              className='eventDescriptionUpload'
              type='text'
              placeholder='Event description...'
              name='description'
              value={values.description}
              onChange={handleChange}
            />
          </div>
          <div className='rightBox'>
            <div className='trackingInput'>
              <div className='dateInput'>
                <input
                  className='dateInput'
                  type='date'
                  placeholder='mm-dd-yyyy'
                  name='date'
                  value={values.date}
                  onChange={handleChange}
                />
              </div>
              <div className='timeInput'>
                <input
                  className='timeInputStart'
                  type='text'
                  placeholder='00:00'
                  name='startTime'
                  value={values.startTime}
                  onChange={handleChange}
                />
                <input
                  className='timeInputEnd'
                  type='text'
                  placeholder='00:00'
                  name='endTime'
                  value={values.endTime}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className='filterInput'>
              idk what to do for this tbh
            </div>
            <button type='submit' className='postEvent'>Post Event!</button>   
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreateEvent;
